      if (materialUpdateStateString !== DrawKickerWorld._lastMaterialUpdateStateString || DrawKickerWorld._lastRenderPassIndex !== renderPassIndex) {
        if (materials[i]) {
          isMaterialSetupDone = materials[i].setUp();
        }
      }

      if (!isMaterialSetupDone) {
