      v2 = matchArray[4];
      vn2 = matchArray[5];
      v3 = matchArray[6];
      vn3 = matchArray[7];
      positions[fCount*3] = pvCoord[v1-1];
      positions[fCount*3+1] = pvCoord[v2-1];
      positions[fCount*3+2] = pvCoord[v3-1];
      normals[fCount*3] = pvNormal[vn1-1];
      normals[fCount*3+1] = pvNormal[vn2-1];
